var App = require('./app');

App.Router.map(function() {

  // generated by ember-generate --scaffold
  this.resource('products');
  this.resource('product', {path: '/products/:product_id'});
  this.route('edit_product', {path: '/products/:product_id/edit'});
  this.route('new_product', {path: '/products/new'});
  // end generated routes

  this.resource('sessions', function() {
    this.route('new');
  });
  this.resource('users', function() {
    this.route('new');
  })
  this.route('top_secret');
});
